INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (115, 23608, 'Ixazomib', 'Food may reduce the oral bioavailability of ixazomib.  A food effect study found that administration of a single 4 mg dose of ixazomib with a high-fat meal decreased ixazomib peak plasma concentration (Cmax) by 69% and systemic exposure (AUC) by 28%.', 'high-fat foods', 'Moderate', 'Ixazomib should be taken at least one hour before or two hours after eating.  On days when both ixazomib and dexamethasone are administered, advise patients to separate dosing times, since dexamethasone should be taken with food while ixazomib should be taken on an empty stomach.', '', '', 'DDInter', 0);
