INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (118, 3182, 'Ketoconazole', 'Excessive use of alcohol or products containing alcohol together with ketoconazole or levoketoconazole may potentiate the risk of liver injury.  Serious hepatotoxicity has been reported with levoketoconazole.  Fatal hepatotoxicity or requiring liver transplantation have been reported with the use of oral ketoconazole, of which levoketoconazole is an enantiomer.', 'alcohol', 'Moderate', 'Excessive consumption of alcohol should generally be avoided during ketoconazole or levoketoconazole therapy.  Patients receiving ketoconazole or levoketoconazole should be instructed to contact their doctor immediately if they experience swelling, skin rash, itching, loss of appetite, fatigue, nausea, vomiting, abdominal pain, dark colored urine, light colored stools, and/or yellowing of the skin or eyes, as these may be signs and symptoms of liver damage.', '', '', 'DDInter', 0);
