INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (158, 21596, 'Lanthanum carbonate', 'Lanthanum carbonate should be administered with food for therapeutic efficacy.  However, it is insoluble in water (<0.01 mg/mL at pH 7.5) and therefore cannot be dissolved in liquid for administration through an enteral feeding tube, because it may result in blockage of the tube.', 'food', 'Moderate', 'Administration of lanthanum carbonate with enteral feedings is not recommended.  Alternative medications such as calcium carbonate suspension should be considered.', '', '', 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (159, 11771, 'Lapatinib', 'Grapefruit juice may increase the plasma concentrations of lapatinib.  The proposed mechanism is inhibition of CYP450 3A4-mediated first-pass metabolism in the gut wall by certain compounds present in grapefruits.', 'grapefruit juice', 'Moderate', 'Patients treated with lapatinib should preferably avoid the consumption of grapefruit or grapefruit juice.  The manufacturer recommends that lapatinib be administered at least one hour before or one hour after a meal.  The lapatinib dose is administered once daily and should not be divided.', '', '', 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (160, 0, 'Lanreotide', 'Due to their gastrointestinal pharmacologic effects, somatostatin analogs (e.g., octreotide, lanreotide) may variously affect the absorption of dietary nutrients and concomitantly administered oral medications.  Somatostatin analogs have been shown to prolong gastrointestinal transit time and inhibit intestinal absorption of some nutrients such as fat.', 'food', 'Moderate', 'Clinicians should be aware of the potential for altered absorption of concomitantly administered oral medications during treatment with somatostatin analogs.  Blood levels and clinical response should be monitored, particularly for drugs that have a narrow therapeutic index, and the dosages adjusted as necessary.', '', '', 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (161, 0, 'Larotrectinib', 'Grapefruit juice may increase the plasma concentrations of larotrectinib.  The proposed mechanism is inhibition of CYP450 3A4-mediated metabolism of larotrectinib by certain compounds present in grapefruit.', 'grapefruit juice', 'Moderate', 'Larotrectinib may be taken with or without food.  Patients should avoid the consumption of grapefruit and grapefruit juice during treatment.', '', '', 'DDInter', 0);
