INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (583, 616, 'Losartan', 'Moderate-to-high dietary intake of potassium, especially salt substitutes, may increase the risk of hyperkalemia in some patients who are using angiotensin II receptor blockers (ARBs).  ARBs can promote hyperkalemia through inhibition of angiotensin II-induced aldosterone secretion.  Patients with diabetes, heart failure, dehydration, or renal insufficiency have a greater risk of developing hyperkalemia.', 'Food High in Potassium', 'Moderate', 'Patients who regularly consume grapefruits and grapefruit juice should be monitored for altered efficacy of losartan.  Grapefruits and grapefruit juice should be avoided if an interaction is suspected.  Orange juice is not expected to interact.', '', '', 'DDInter', 0);
