INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (708, 0, 'Mesoridazine', 'Concurrent use of ethanol and phenothiazines may result in additive CNS depression and psychomotor impairment.  Also, ethanol may precipitate dystonic reactions in patients who are taking phenothiazines.', 'alcohol', 'Moderate', 'Patients should be advised to avoid alcohol during phenothiazine therapy.', '', '', 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (709, 22745, 'Meprobamate', 'Alcohol may potentiate some of the pharmacologic effects of CNS-active agents.  Use in combination may result in additive central nervous system depression and/or impairment of judgment, thinking, and psychomotor skills.', 'alcohol', 'Moderate', 'Patients receiving CNS-active agents should be warned of this interaction and advised to avoid or limit consumption of alcohol.  Ambulatory patients should be counseled to avoid hazardous activities requiring complete mental alertness and motor coordination until they know how these agents affect them, and to notify their physician if they experience excessive or prolonged CNS effects that interfere with their normal activities.', '', '', 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (710, 0, 'Mepenzolate', 'Use of anticholinergic agents with alcohol may result in sufficient impairment of attention so as to render driving and operating machinery more hazardous.  In addition, the potential for abuse may be increased with the combination.  The mechanism of interaction is not established but may involve additive depressant effects on the central nervous system.', 'alcohol', 'Moderate', 'Alcohol should generally be avoided during therapy with anticholinergic agents.  Patients should be counseled to avoid activities requiring mental alertness until they know how these agents affect them.', '', '', 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (711, 25505, 'Mercaptopurine', 'Limited data suggest that food may decrease the oral bioavailability of 6-mercaptopurine (6-MP).', 'food', 'Moderate', 'Until more information is available regarding the effect of food on 6-MP absorption, it may be advisable to take 6-MP on an empty stomach 1 hour before or 2 hours after a meal.', '', '', 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (712, 0, 'Metamfetamine', 'Alcohol may potentiate the central nervous system and cardiovascular effects of centrally-acting appetite suppressants.', 'alcohol', 'Moderate', 'Concomitant use of centrally-acting appetite suppressants and alcohol should be avoided if possible, especially in patients with a history of cardiovascular disease.  Patients should be counselled to avoid hazardous activities requiring complete mental alertness and motor coordination until they know how these agents affect them, and to notify their physician if they experience excessive or prolonged CNS effects that interfere with their normal activities.', '', '', 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (713, 0, 'Meperidine', 'Ethanol may potentiate the central nervous system (CNS) depressant effects of opioid analgesics.', 'alcohol', 'Moderate', 'Concomitant use of opioid analgesics with ethanol should be avoided.', '', '', 'DDInter', 0);
