INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, reference_text, source, created_at, management_text_blob, mechanism_text_blob, interaction_blob) VALUES (1001, 3846, 'Midazolam', 'The pharmacologic activity of oral midazolam, triazolam, and alprazolam may be increased if taken after drinking grapefruit juice.  The proposed mechanism is CYP450 3A4 enzyme inhibition.  In addition, acute alcohol ingestion may potentiate CNS depression and other CNS effects of many benzodiazepines.  Tolerance may develop with chronic ethanol use.  The mechanism may be decreased clearance of the benzodiazepines because of CYP450 hepatic enzyme inhibition.  Also, it has been suggested that the cognitive deficits induced by benzodiazepines may be increased in patients who chronically consume large amounts of alcohol.', 'grapefruit juice', 'Moderate', '', 'DDInter', 0, X'', NULL, X'');
