INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (1015, 0, 'Midostaurin', 'Grapefruit juice may significantly increase the plasma concentrations of midostaurin.  The proposed mechanism is inhibition of CYP450 3A4-mediated first-pass metabolism in the gut wall by certain compounds present in grapefruit.  In general, the effect of grapefruit juice is concentration-, dose- and preparation-dependent, and can vary widely among brands.', 'grapefruit juice', 'Major', 'The manufacturer recommends taking midostaurin with food.  Midostaurin was administered with food in clinical trials.  Patients should avoid consumption of grapefruit and grapefruit juice during treatment with midostaurin.', '', '', 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (1016, 0, 'Mifepristone', 'Food may significantly increase the plasma concentrations of mifepristone.', 'grapefruit juice', 'Moderate', 'When mifepristone is used daily to control hyperglycemia secondary to hypercortisolism in patients with endogenous Cushing''s syndrome, it should be taken with food to achieve consistent plasma drug levels.  Patients should be advised to avoid consuming grapefruit or grapefruit juice during treatment with mifepristone, as it may cause increased adverse effects such as headache, dizziness, fatigue, nausea, vomiting, cramping, diarrhea, hypokalemia, adrenal insufficiency, vaginal bleeding, arthralgia, peripheral edema, and hypertension.  Because mifepristone is eliminated slowly from the body, the interaction with grapefruit juice may be observed for a prolonged period.', '', '', 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (1017, 0, 'Mipomersen', 'Coadministration with alcohol may increase the risk of hepatotoxicity associated with the use of mipomersen.', 'alcohol', 'Major', 'Since alcohol may increase levels of hepatic fat and induce or exacerbate liver injury, the manufacturer recommends that patients taking mipomersen not consume more than one alcoholic drink per day.', '', '', 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (1018, 2385, 'Mirabegron', 'Food reduces the oral absorption and bioavailability of mirabegron.  According to the product labeling, administration of a 50 mg tablet with a high-fat meal decreased mirabegron peak plasma concentration (Cmax) and systemic exposure (AUC) by 45% and 17%, respectively, whereas administration with a low-fat meal decreased mirabegron Cmax and AUC by 75% and 51%, respectively.', 'high-fat foods', 'Minor', 'In phase 3 clinical studies demonstrating both safety and efficacy, mirabegron was administered without regards to food content and intake.  Therefore, mirabegron can be taken with or without food at the recommended dosage.', '', '', 'DDInter', 0);
