INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (1098, 24196, 'Mycophenolic acid', 'Administration of enteric coated mycophenolic acid with meals may alter its pharmacokinetics relative to administration in the fasting state.  When mycophenolic acid 720 mg was administered with a high-fat meal, there was a 33% decrease in the peak plasma concentration (Cmax); a 3.5-hour increase in delay time for the rise of plasma mycophenolic acid; and a 5-hour delay in the time to reach peak plasma concentration (Tmax).  However, no effect was observed on the systemic exposure of mycophenolic acid.', 'high-fat foods', 'Moderate', 'To avoid variability in drug absorption between doses, enteric coated formulations of mycophenolic acid should be taken on an empty stomach, one hour before or two hours after food intake.  The tablets should be swallowed whole and not crushed, chewed or divided in order to maintain the integrity of the enteric coating.', '', '', 'DDInter', 0);
