INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (1112, 21415, 'Asparaginase Erwinia chrysanthemi', 'Concomitant use of asparaginase with other hepatotoxic agents may potentiate the risk of liver injury.  Asparaginase-associated hepatotoxicity has been reported more commonly in adults than in children and has been strongly associated with obesity.  Hepatomegaly, acute severe hepatotoxicity, and fatal liver failure have been reported with asparaginase treatment in adults.  Also, asparaginase may increase the toxicity of drugs bound to plasma proteins or metabolized by the liver.', 'alcohol', 'Moderate', 'The risk of additive hepatotoxicity should be considered when asparaginase is used with other hepatotoxic agents (e.g., alcohol, androgens, antituberculosis agents, azole antifungal agents, ACE inhibitors, macrolide antibiotics, nonsteroidal anti-inflammatory agents, nucleoside reverse transcriptase inhibitors, sulfonamides, thiazolidinediones, and statins).  Liver function tests should be monitored at regular intervals during asparaginase treatment with or without other hepatotoxic drugs.  Patients should be advised to seek medical attention if they experience potential symptoms of hepatotoxicity such as right upper quadrant pain, increasing abdominal size, fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, dark urine, pale stools, and jaundice.', '', '', 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (1113, 0, 'Naldemedine', 'Grapefruit juice may increase the plasma concentrations of naldemedine.  The proposed mechanism is inhibition of CYP450 3A4-mediated first-pass metabolism in the gut wall by certain compounds present in grapefruit.', 'grapefruit juice', 'Moderate', 'Naldemedine may be taken with or without food.  Patients should avoid consumption of grapefruit and grapefruit juice during treatment with naldemedine.', '', '', 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (1114, 244, 'Nalbuphine', 'Ethanol may potentiate the central nervous system (CNS) depressant effects of opioid analgesics.  Concomitant use may result in additive CNS depression and impairment of judgment, thinking, and psychomotor skills.  In more severe cases, hypotension, respiratory depression, profound sedation, coma, or even death may occur.', 'alcohol', 'Moderate', 'Concomitant use of opioid analgesics with ethanol should be avoided.', '', '', 'DDInter', 0);
