INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, reference_text, source, created_at, management_text_blob, mechanism_text_blob, interaction_blob) VALUES (716, 614, 'Metformin', 'Alcohol can potentiate the effect of metformin on lactate metabolism and increase the risk of lactic acidosis.  In addition, alcohol may cause hypoglycemia or hyperglycemia in patients with diabetes.  Although hypoglycemia rarely occurs during treatment with metformin alone, the risk may increase with acute consumption of alcohol.  Even modest amounts can lower blood sugar significantly, especially when the alcohol is ingested on an empty stomach or following exercise.  The mechanism involves inhibition of both gluconeogenesis as well as the counter-regulatory response to hypoglycemia.', 'alcohol', 'Major', '', 'DDInter', 0, X'', NULL, X'');
