INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (1275, 0, 'Atazanavir', 'Administration of atazanavir with food enhances oral bioavailability and reduces pharmacokinetic variability.The coefficient of variation of AUC and Cmax decreased by approximately one-half when given with either a light or high-fat meal compared to the fasting state.', 'high-fat foods', 'Moderate', 'To ensure maximal oral absorption, atazanavir should be administered with or immediately after a meal.', '', '', 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (1276, 4285, 'Norfloxacin', 'Concurrent ingestion of meals, dairy products (milk, yogurt) or calcium-fortified foods (i.e., cereal, orange juice) may decrease the absorption of oral norfloxacin.  The mechanism is chelation of calcium and the quinolone, resulting in decreased bioavailability.  In the case of orange juice, inhibition of intestinal transport mechanisms (P-glycoprotein or organic anion-transporting polypeptides) by flavones may also be involved.', 'orange juice', 'Moderate', 'Oral norfloxacin should be taken at least one hour before or two hours after a meal, milk, or other dairy products or calcium-fortified foods.', '', '', 'DDInter', 0);
