INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (1603, 4454, 'Paclitaxel (protein-bound)', 'Coadministration with inhibitors of CYP450 3A4, such as grapefruit juice, may increase the plasma concentrations of paclitaxel, which is a substrate of the isoenzyme.  Current data suggest that consumption of large quantities of grapefruit juice inhibit both intestinal and hepatic CYP450 3A4 due to certain compounds present in grapefruit.', 'grapefruit juice', 'Moderate', 'Caution is recommended if paclitaxel is to be used in combination with grapefruit and grapefruit juice.  Patients should be closely monitored for the development of paclitaxel toxicity, including diarrhea, mucositis, myelosuppression, and peripheral neuropathy and dose adjustment considered per local treatment protocols.', '', '', 'DDInter', 0);
