INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (1708, 24417, 'Oxycodone', 'Alcohol may potentiate the central nervous system (CNS) depressant effects of opioid analgesics including oxycodone.  Concomitant use may result in additive CNS depression and impairment of judgment, thinking, and psychomotor skills.  In more severe cases, hypotension, respiratory depression, profound sedation, coma, or even death may occur.', 'alcohol', 'Major', 'Patients should not consume alcoholic beverages or use drug products that contain alcohol during treatment with oxycodone.  Any history of alcohol or illicit drug use should be considered when prescribing oxycodone, and therapy initiated at a lower dosage if necessary.  Patients should be closely monitored for signs and symptoms of sedation, respiratory depression, and hypotension.  Due to a high degree of interpatient variability with respect to grapefruit juice interactions, patients treated with oxycodone may also want to avoid or limit the consumption of grapefruit and grapefruit juice.', '', '', 'DDInter', 0);
