INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (1757, 29604, 'Pazopanib', 'Grapefruit juice may increase the plasma concentrations of pazopanib.  The proposed mechanism is inhibition of CYP450 3A4-mediated first-pass metabolism in the gut wall by certain compounds present in grapefruits.', 'grapefruit juice', 'Major', 'Patients treated with pazopanib should avoid consumption of grapefruit, grapefruit juice, and any supplement containing grapefruit extract.  Pazopanib should be administered at least one hour before or two hours after a meal.', '', '', 'DDInter', 0);
