INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (1804, 20843, 'Axitinib', 'Grapefruit juice may increase the plasma concentrations of axitinib.  The proposed mechanism is inhibition of CYP450 3A4-mediated first-pass metabolism in the gut wall by certain compounds present in grapefruit.', 'grapefruit juice', 'Moderate', 'Patients treated with axitinib should avoid consumption of grapefruit, grapefruit juice, and any supplement containing grapefruit extract.  Axitinib may be administered with or without food.', '', '', 'DDInter', 0);
