INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (1806, 0, 'Pindolol', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation.  Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', 'alcohol', 'Moderate', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.  Some authorities recommend avoiding alcohol in patients receiving vasodilating antihypertensive drugs.  Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', '', '', 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (1807, 24220, 'Pimozide', 'Theoretically, the coadministration with grapefruit juice may increase the plasma concentrations of pimozide.  The mechanism is decreased clearance of pimozide due to inhibition of CYP450 3A4-mediated first-pass metabolism in the gut wall by certain compounds present in grapefruits.', 'grapefruit juice', 'Major', 'The manufacturer recommends avoiding grapefruit juice (and probably grapefruits) during therapy with pimozide.  Patients should also be advised to avoid or limit consumption of alcohol.', '', '', 'DDInter', 0);
