INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (1866, 0, 'Pomalidomide', 'Cigarette smoking may reduce pomalidomide exposure due to induction of CYP450 1A2, the isoenzyme that is responsible for the metabolic clearance of pomalidomide along with CYP450 3A4.', 'cigarette', 'Moderate', 'Patients should be advised that smoking may reduce the efficacy of pomalidomide therapy.  Pomalidomide should be taken on an empty stomach, at least 2 hours before or 2 hours after a meal.', '', '', 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (1867, 224, 'Plantago seed', 'Bulk-forming laxatives such as guar gum, psyllium, polycarbophil, or methylcellulose may affect the bioavailability of concomitantly administered substances due to their effect on the rate of gastrointestinal transit and gastric emptying.', 'food', 'Moderate', 'In general, oral medications should be administered at least 2 hours before or 2 hours after dosing of the bulk-forming laxative.', '', '', 'DDInter', 0);
