INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (2800, 25708, 'Sirolimus', 'Consumption of food can decrease the rate and extent of gastrointestinal absorption of sirolimus.   Also, the consumption of grapefruit juice may result in increased sirolimus trough concentrations.', 'grapefruit juice', 'Moderate', 'Experts recommend that this drug be taken either at least one hour prior to eating or consistently with or without food to avoid variations in sirolimus blood levels.  The manufacturer recommends against using grapefruit juice for dilution of sirolimus doses.  Patients should be monitored for clinical and laboratory evidence of altered immunosuppressant effects.', '', '', 'DDInter', 0);