INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (2805, 27304, 'Sorafenib', 'Food may reduce the oral absorption and bioavailability of sorafenib.  According to the product labeling, sorafenib bioavailability was reduced by 29% when administered with a high-fat meal compared to administration in the fasted state.  When given with a moderate-fat meal, bioavailability was similar to that in the fasted state.', 'high-fat foods', 'Moderate', 'To ensure maximal and consistent oral absorption, sorafenib should be taken at least one hour before or two hours after eating.', '', '', 'DDInter', 0);
