INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (2827, 5365, 'Sucralfate', 'The administration of sucralfate with enteral nutrition may result in precipitation and the formation of bezoars that may obstruct feeding tubes.  The proposed mechanism is the formation of an insoluble aluminum-protein complex between the aluminum in the sucralfate and the protein in the enteral feeding.  In addition, sucralfate may not become activated due to the alkaline pH of enteral nutrition products.', 'food', 'Moderate', 'Some experts recommend separating the sucralfate doses from enteral feedings by at least 1 hour.  Alternatively, consideration may be given to proton pump inhibitors or H2 antagonists.', '', '', 'DDInter', 0);
