INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (2994, 684, 'Theophylline', 'Coadministration with caffeine may increase the serum concentrations of theophylline.  The proposed mechanism involves competitive inhibition of theophylline metabolism via CYP450 1A2, as well as metabolic conversion of caffeine to theophylline in vivo and saturation of theophylline metabolism at higher serum concentrations.', 'coffee', 'Moderate', 'When administered to patients receiving continuous enteral nutrition , some experts recommend that the tube feeding should be interrupted for at least 1 hour before and 1 hour after the dose of theophylline is given; rapid-release formulations are preferable, and theophylline levels should be monitored.', '', '', 'DDInter', 0);
