INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (3056, 0, 'Tolevamer', 'Potassium in foods can bind to the cation exchange resin and interfere with potassium removal in the treatment of hyperkalemia.', 'Food High in Potassium', 'Moderate', 'To minimize the risk of interaction, patients should be advised to separate the dosing of the cation exchange resin from other orally administered medications by at least 3 hours.  The dosing interval should be increased to 6 hours for patients with gastroparesis or other conditions resulting in delayed emptying of food from the stomach into the small intestine.  Health care professionals should monitor blood levels and/or clinical response to the other medications when appropriate.', '', '', 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (3057, 23739, 'Tolvaptan', 'Grapefruit juice may significantly increase the plasma concentrations of tolvaptan.  The proposed mechanism is inhibition of CYP450 3A4-mediated first-pass metabolism in the gut wall by certain compounds present in grapefruits.', 'grapefruit juice', 'Moderate', 'Patients treated with tolvaptan should avoid consumption of grapefruits and grapefruit juice.', '', '', 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (3058, 3015, 'Tolterodine', 'Use of anticholinergic agents with alcohol may result in sufficient impairment of attention so as to render driving and operating machinery more hazardous.  In addition, the potential for abuse may be increased with the combination.  The mechanism of interaction is not established but may involve additive depressant effects on the central nervous system.', 'alcohol', 'Moderate', 'Alcohol should generally be avoided during therapy with anticholinergic agents.  Patients should be counseled to avoid activities requiring mental alertness until they know how these agents affect them.', '', '', 'DDInter', 0);
