INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (3135, 0, 'Triazolam', 'The pharmacologic activity of oral midazolam, triazolam, and alprazolam may be increased if taken after drinking grapefruit juice.  The proposed mechanism is CYP450 3A4 enzyme inhibition.', 'grapefruit juice', 'Moderate', 'The manufacturer recommends that grapefruit juice should not be taken with oral midazolam.  Patients taking triazolam or alprazolam should be monitored for excessive sedation.  Alternatively, the patient could consume orange juice which does not interact with these drugs.  Patients should be advised to avoid alcohol during benzodiazepine therapy.', '', '', 'DDInter', 0);
