INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (3201, 21108, 'Verapamil', 'Consumption of large quantities of grapefruit juice may be associated with significantly increased plasma concentrations of oral verapamil.  The mechanism is inhibition of CYP450 3A4-mediated first-pass metabolism in the gut wall by certain compounds present in grapefruits.', 'grain', 'Moderate', 'Patients treated with oral verapamil should avoid the consumption of large amounts of grapefruit or grapefruit juice to prevent any undue fluctuations in serum drug levels.  Patients should be advised to seek medical attention if they experience edema or swelling of the lower extremities; sudden, unexplained weight gain; difficulty breathing; chest pain or tightness; or hypotension as indicated by dizziness, fainting, or orthostasis.', '', '', 'DDInter', 0);
