INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (3378, 28911, 'Valganciclovir', 'Food increases the bioavailability of ganciclovir from the prodrug, valganciclovir.  In 16 HIV-positive subjects, the administration of valganciclovir 875 mg once daily with a high-fat meal containing approximately 600 calories resulted in a 30% increase in the steady-state area under the plasma concentration-time curve (AUC) and a 14% increase in the peak plasma concentration (Cmax) of ganciclovir, with no delay in the time to reach peak plasma concentration (Tmax).', 'high-fat foods', 'Moderate', 'The manufacturer recommends that valganciclovir be taken with meals.', '', '', 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (3379, 0, 'Vibrio cholerae CVD 103-HgR strain live antigen (live)', 'There are no data regarding the concurrent administration of cholera oral vaccine with food or beverages.', 'food', 'Moderate', 'The manufacturer recommends that patients avoid eating or drinking for 60 minutes before and after administration of cholera oral vaccine.', '', '', 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (3380, 29091, 'Venetoclax', 'Food enhances the oral bioavailability of venetoclax.  Relative to fasting conditions, venetoclax systemic exposure (AUC) increased by approximately 3.4-fold when administered with a low-fat meal and by 5.1- to 5.3-fold when administered with a high-fat meal.', 'high-fat foods', 'Major', 'Venetoclax should be administered with a meal and water at approximately the same time each day.  Patients should avoid consumption of grapefruit products, Seville oranges, and starfruit during treatment with venetoclax.', '', '', 'DDInter', 0);
