INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (3499, 10558, 'Zaleplon', 'Alcohol may potentiate some of the pharmacologic effects of zaleplon.  Use in combination may result in additive central nervous system depression and/or impairment of judgment, thinking, and psychomotor skills.', 'alcohol', 'Moderate', 'Patients receiving zaleplon should be advised to avoid the consumption of alcohol.  For faster sleep onset, zaleplon should not be administered with or immediately after a high-fat or heavy meal.', '', '', 'DDInter', 0);
