INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (3504, 453, 'Zafirlukast', 'Food may reduce the oral absorption and bioavailability of zafirlukast.  In two separate studies, one using a high-fat and the other a high-protein meal, administration of zafirlukast with food reduced the mean bioavailability by approximately 40%.', 'high-fat foods', 'Moderate', 'To ensure maximal oral absorption, zafirlukast should be administered at least 1 hour before or 2 hours after meals.', '', '', 'DDInter', 0);
