INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (3562, 0, 'Betrixaban', 'Food reduces the oral bioavailability of betrixaban.  When administered with a low-fat (900 calories; 20% fat) or high-fat (900 calories; 60% fat) meal, betrixaban peak plasma concentration (Cmax) and systemic exposure (AUC) decreased relative to administration in the fasting state by an average of 70% and 61%, respectively, with the low-fat meal and 50% and 48%, respectively, with the high-fat meal.  The effect of food on betrixaban pharmacokinetics could be observed for up to 6 hours after meal intake.', 'high-fat foods', 'Moderate', 'The manufacturer recommends taking betrixaban at the same time each day with food.', '', '', 'DDInter', 0);
