INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (3695, 1801, 'Buspirone', 'Alcohol may potentiate some of the pharmacologic effects of buspirone.  Use in combination may result in additive central nervous system depression and/or impairment of judgment, thinking, and psychomotor skills.', 'alcohol', 'Moderate', 'Patients receiving buspirone should be advised to avoid consumption of alcohol.  Patients also should preferably avoid the consumption of large amounts of grapefruits and grapefruit juice to prevent any undue fluctuations in plasma drug levels.  If this is not possible, the buspirone dose should be taken at least 2 hours before or 8 hours after grapefruit or grapefruit juice.  Monitoring for increased CNS depression is recommended.', '', '', 'DDInter', 0);
