INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (4017, 16063, 'Ceftibuten', 'Food reduces the oral absorption and bioavailability of ceftibuten.  According to the product labeling, administration of ceftibuten oral suspension with a high-fat breakfast decreased the mean peak plasma concentration (Cmax) and area under the concentration-time curve (AUC) by 26% and 17%, respectively.', 'high-fat foods', 'Moderate', 'To ensure maximal oral absorption, ceftibuten oral suspension should be taken at least two hours before or one hour after a meal.', '', '', 'DDInter', 0);
