INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (4147, 16649, 'Chlorthalidone', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation.  Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', 'alcohol', 'Moderate', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.  Some authorities recommend avoiding alcohol in patients receiving vasodilating antihypertensive drugs.  Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', '', '', 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (4148, 274, 'Cinacalcet', 'Food enhances the oral absorption and bioavailability of cinacalcet.  According to the product labeling for cinacalcet, administration with a high-fat meal increased the peak plasma concentration (Cmax) and area under the concentration-time curve (AUC) of cinacalcet by 82% and 68%, respectively, compared to administration in the fasting state.  The Cmax and AUC were increased 65% and 50%, respectively, when cinacalcet was administered with a low-fat meal compared to fasting.', 'high-fat foods', 'Moderate', 'To ensure maximal oral absorption, cinacalcet should be administered with or immediately after a meal.  The tablets should be taken whole and not divided or crushed.', '', '', 'DDInter', 0);
