INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, reference_text, source, created_at, management_text_blob, mechanism_text_blob, interaction_blob) VALUES (2286, 25569, 'Ritonavir', 'Administration with food may modestly affect the bioavailability of ritonavir from the various available formulations.  When the oral solution was given under nonfasting conditions, peak ritonavir concentrations decreased 23% and the extent of absorption decreased 7% relative to fasting conditions.  Dilution of the oral solution (within one hour of dosing) with 240 mL of chocolate milk or a nutritional supplement (Advera or Ensure) did not significantly affect the extent and rate of ritonavir absorption.  When a single 100 mg dose of the tablet was administered with a high-fat meal (907 kcal; 52% fat, 15% protein, 33% carbohydrates), approximately 20% decreases in mean peak concentration (Cmax) and systemic exposure (AUC) were observed relative to administration after fasting.  Similar decreases in Cmax and AUC were reported when the tablet was administered with a moderate-fat meal.  In contrast, the extent of absorption of ritonavir from the soft gelatin capsule formulation was 13% higher when administered with a meal (615 KCal; 14.5% fat, 9% protein, and 76% carbohydrate) relative to fasting.', 'high-fat foods', 'Moderate', '', 'DDInter', 0, X'789c0dc9d10d80300805c055de040ee30654494b4448ca53e3f67abfb71a33e4b6891a79f98ea6a01c1a788c03a78a1798d018129ba24b71a605b56821fe9feb94666e7c970f8e821dd8', NULL, X'');
