INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (4366, 720, 'Clomipramine', 'Limited data suggest that the administration of clomipramine with grapefruit juice or cranberry juice may significantly increase plasma drug concentrations of clomipramine.  Clomipramine is initially demethylated by CYP450 1A2, 3A3 and 3A4 before undergoing further metabolism to 8-hydroxyclomipramine.  The increase in clomipramine bioavailability may stem from inhibition of CYP450 3A4-mediated first-pass metabolism in the gut wall by certain compounds present in grapefruits.  The precise mechanism by which cranberry juice exerts its effects is unknown, but may involve inhibition of CYP450 isoenzymes.  This interaction has occasionally been exploited in attempts to improve symptomatic control of obsessive compulsive disorder.', 'grapefruit juice', 'Moderate', 'Patients receiving clomipramine therapy who ingest cranberry juice, grapefruits, or grapefruit juice should be monitored for adverse effects and undue fluctuations in plasma drug levels.', '', '', 'DDInter', 0);
