INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (4420, 0, 'Cobimetinib', 'Grapefruit juice may increase the plasma concentrations of orally administered drugs that are substrates of the CYP450 3A4 isoenzyme, such as cobimetinib.  However, the interaction seems to affect primarily those drugs that undergo significant presystemic metabolism by CYP450 3A4 (i.e., drugs with low oral bioavailability), presumably due to the fact that grapefruit juice inhibits primarily intestinal rather than hepatic CYP450 3A4.', 'grapefruit juice', 'Moderate', 'Patients who regularly consume grapefruit or grapefruit juice should be monitored for adverse effects and altered plasma concentrations of cobimetinib.  Grapefruit and grapefruit juice should be avoided if an interaction is suspected.  Orange juice is not expected to interact with these drugs.', '', '', 'DDInter', 0);
