INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (4524, 4889, 'Deflazacort', 'Grapefruit juice may increase the plasma concentrations of 21-desdeflazacort, the active metabolite of deflazacort that is formed by esterases after oral administration and further metabolized by CYP450 3A4 to several inactive metabolites.  The proposed mechanism is inhibition of CYP450 3A4-mediated metabolism in the gut wall by certain compounds present in grapefruit.  In general, the effect of grapefruit juice is concentration-, dose- and preparation-dependent, and can vary widely among brands.', 'grapefruit juice', 'Moderate', 'Deflazacort should not be administered with grapefruit juice.', '', '', 'DDInter', 0);
