INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (4529, 6700, 'Desmopressin', 'Food may decrease the rate and extent of absorption of desmopressin following oral administration.', 'food', 'Minor', 'This interaction has a low clinical impact in most patients, especially since oral desmopressin is intended to be administered at bedtime.Nevertheless, the possibility of food effects should be considered before increasing the dose whenever a diminution of effect is noted.  A significant interaction is not expected to occur with the sublingual formulation, since absorption occurs primarily in the oral mucosa, pharynx, and esophagus.', '', '', 'DDInter', 0);
