INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (4564, 0, 'Dextroamphetamine', 'Alcohol may potentiate the cardiovascular effects of amphetamines.', 'alcohol', 'Moderate', 'Concomitant use of amphetamines and alcohol should be avoided if possible, especially in patients with a history of heart disease.', '', '', 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (4565, 0, 'Desipramine', 'Concomitant use of ethanol and a tricyclic antidepressant (TCA) may result altered TCA plasma levels and efficacy, and additive impairment of motor skills, especially driving skills.  Acute ethanol ingestion may inhibit TCA metabolism, while chronic ingestion of large amounts of ethanol may induce hepatic TCA metabolism.', 'alcohol', 'Moderate', 'Patients should be advised to avoid alcohol during TCA therapy.  Alcoholics who have undergone detoxification should be monitored for decreased TCA efficacy.  Dosage adjustments may be required.', '', '', 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (4566, 14420, 'Alpelisib', 'Food significantly enhances the oral absorption and bioavailability of alpelisib.  When administered with a high-fat high-calorie meal (985 calories with 58.1 g of fat) or a low-fat low-calorie meal (334 calories with 8.7 g of fat) the AUC and Cmax of a single dose of alpelisib was increased by 73% and 84% and 77% and 145%, respectively.  There were no clinically significant differences in alpelisib AUC between the two types of meals.  In addition, food appears to have a more pronounced effect on the solubility of alpelisib than gastric pH.', 'high-fat foods', 'Moderate', 'To ensure maximal oral absorption, alpelisib should be administered with a meal.', '', '', 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (4567, 0, 'Dextropropoxyphene', 'Alcohol may have additive CNS- and/or respiratory-depressant effects with propoxyphene.  Misuse of propoxyphene, either alone or in combination with other CNS depressants, has been a major cause of drug-related deaths, particularly in patients with a history of emotional disturbances, suicidal ideation, or alcohol and drug abuse.', 'alcohol', 'Major', 'The use of alcohol during propoxyphene therapy should be avoided.  Patients should be warned not to exceed the recommended dosage of propoxyphene and to avoid activities requiring mental alertness until they know how these agents affect them.', '', '', 'DDInter', 0);
