INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (4795, 3681, 'Digoxin', 'Administration of digoxin with a high-fiber meal has been shown to decrease its bioavailability by almost 20%.  Fiber can sequester up to 45% of the drug when given orally.', 'high-fiber meal', 'Minor', 'Patients should be advised to maintain a regular diet without significant fluctuation in fiber intake while digoxin is being titrated.', '', '', 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (4796, 0, 'Diethylpropion', 'Alcohol may potentiate the central nervous system and cardiovascular effects of centrally-acting appetite suppressants.', 'alcohol', 'Moderate', 'Concomitant use of centrally-acting appetite suppressants and alcohol should be avoided if possible, especially in patients with a history of cardiovascular disease.  Patients should be counselled to avoid hazardous activities requiring complete mental alertness and motor coordination until they know how these agents affect them, and to notify their physician if they experience excessive or prolonged CNS effects that interfere with their normal activities.', '', '', 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (4797, 13982, 'Dicloxacillin', 'Certain penicillins may exhibit reduced gastrointestinal absorption in the presence of food.  The therapeutic effect of the antimicrobial may be reduced.', 'food', 'Moderate', 'The interacting penicillin should be administered one hour before or two hours after meals.  Penicillin V and amoxicillin are not affected by food and may be given without regard to meals.', '', '', 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (4798, 22701, 'Dicloxacillin', 'Certain penicillins may exhibit reduced gastrointestinal absorption in the presence of food.  The therapeutic effect of the antimicrobial may be reduced.', 'food', 'Moderate', 'The interacting penicillin should be administered one hour before or two hours after meals.  Penicillin V and amoxicillin are not affected by food and may be given without regard to meals.', '', '', 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (4799, 0, 'Didanosine', 'Didanosine bioavailability is decreased when administered with food.  Loss of efficacy may result.', 'food', 'Moderate', 'Didanosine should be administered in the fasting state, at least 30 minutes before or more than 2 hours after eating.', '', '', 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (4800, 10774, 'Dicyclomine', 'Use of anticholinergic agents with alcohol may result in sufficient impairment of attention so as to render driving and operating machinery more hazardous.  In addition, the potential for abuse may be increased with the combination.  The mechanism of interaction is not established but may involve additive depressant effects on the central nervous system.', 'alcohol', 'Moderate', 'Alcohol should generally be avoided during therapy with anticholinergic agents.  Patients should be counseled to avoid activities requiring mental alertness until they know how these agents affect them.', '', '', 'DDInter', 0);