INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (4831, 0, 'Diroximel fumarate', 'Coadministration of diroximel fumarate with ethanol may reduce the plasma concentrations of monomethyl fumarate (MMF), the active metabolite of diroximel fumarate.', 'alcohol', 'Moderate', 'The manufacturer recommends avoiding concomitant use of diroximel fumarate with ethanol.', '', '', 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (4832, 0, 'Diroximel fumarate', 'Food does not significantly affect the oral bioavailability of diroximel fumarate.  Administration of diroximel fumarate with a high-fat, high-calorie (900 to 1000 calories; 50% from fat) meal did not affect the systemic exposure (AUC) of its active metabolite, monomethyl fumarate (MMF), but decreased its peak plasma concentration (Cmax) by 44% and prolonged the time to reach peak concentration (Tmax) from 2.5 to 7.0 hours relative to administration in the fasted state.  In comparison, administration of diroximel fumarate with low-fat, low-calorie (350 to 400 calories; 10 to 15 g fat) and medium-fat, medium-calorie (650 to 700 calories; 25 to 30 g fat) meals decreased the MMF Cmax by approximately 12% and 25%, respectively, while also leaving the AUC unaffected.', 'high-fat foods', 'Moderate', 'Diroximel fumarate may be taken with or without food; however, high-fat, high-calorie meals or snacks should be avoided.  The manufacturer recommends meals or snacks containing no more than 700 calories and no more than 30 grams of fat.  Taking diroximel fumarate with food may improve tolerability for patients experiencing flushing or gastrointestinal adverse reactions.  The manufacturer also recommends avoiding concomitant use of diroximel fumarate with ethanol.', '', '', 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (4833, 0, 'Disulfiram', 'Consumption of ethanol during treatment with disulfiram may cause flushing, nausea, blurred vision, dyspnea, tachypnea, tachycardia, and hypotension.  Death has been reported.  The mechanism is probably related to inhibition of aldehyde dehydrogenase, the enzyme responsible for the oxidation of acetaldehyde to acetyl CoA.  Accumulation of acetaldehyde probably results.', 'alcohol', 'Major', 'Ethanol should be avoided in patients receiving disulfiram.', '', '', 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (4834, 14102, 'Dipyridamole', 'Caffeine and other xanthine derivatives (e.g., theophylline) are nonspecific, competitive antagonists of adenosine receptors.  As such, they may interfere with the vasodilating effect of dipyridamole, an adenosine receptor agonist.', 'coffee', 'Moderate', 'Patients should avoid consumption of caffeine-containing products for at least 24 hours prior to administration of dipyridamole for myocardial perfusion imaging.', '', '', 'DDInter', 0);
