INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (4837, 0, 'Disopyramide', 'Ethanol significantly increases the renal elimination of disopyramide, apparently by inducing diuresis (inhibition of antidiuretic hormone).  Limited data show that ethanol does not, however, significantly affect the elimination half-life or total plasma clearance of disopyramide.', 'alcohol', 'Minor', 'No special precautions appear to be necessary.', '', '', 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (4838, 0, 'Disopyramide', 'Grapefruit juice may increase the plasma concentrations of orally administered drugs that are substrates of the CYP450 3A4 isoenzyme.  The proposed mechanism is inhibition of CYP450 3A4-mediated first-pass metabolism in the gut wall by certain compounds present in grapefruit.', 'grapefruit juice', 'Moderate', 'Patients who regularly consume grapefruit or grapefruit juice should be monitored for adverse effects and altered plasma concentrations of drugs that undergo significant presystemic metabolism by CYP450 3A4.  Grapefruit and grapefruit juice should be avoided if an interaction is suspected.  Orange juice is not expected to interact with these drugs.', '', '', 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (4839, 11214, 'Docetaxel', 'Coadministration with inhibitors of CYP450 3A4, such as grapefruit juice, may significantly increase the plasma concentrations of docetaxel, which is a substrate of the isoenzyme.  Current data suggest that consumption of large quantities of grapefruit juice inhibit both intestinal and hepatic CYP450 3A4 due to certain compounds present in grapefruit.', 'grapefruit juice', 'Major', 'The use of docetaxel in combination with grapefruit and grapefruit juice should generally be avoided.  If concomitant use is required, a reduced dosage of docetaxel should be considered, particularly if used with large amounts of grapefruit juice, and therapeutic drug monitoring of docetaxel considered per local treatment protocols.  Patients should be closely monitored for the development of docetaxel toxicity such as myelosuppression, stomatitis, neurotoxicity (e.g., paraesthesia, dysesthesia, pain), myalgia, asthenia, fluid retention, nausea, vomiting, and diarrhea.', '', '', 'DDInter', 0);
