INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (4852, 21657, 'Aluminum hydroxide', 'The concomitant administration of aluminum-containing products (e.g., antacids and phosphate binders) and citrates may significantly increase serum aluminum concentrations, resulting in toxicity.  Citrates or citric acid are contained in numerous soft drinks, citrus fruits, juices, and effervescent and dispersible drug formulations.  Citrates enhance the gastrointestinal absorption of aluminum by an unknown mechanism, which may involve the formation of a soluble aluminum-citrate complex.', 'citrus fruits', 'Major', 'Some experts recommend that antacids should not be mixed with or given after high protein formulations, that the antacid dose should be separated from the feeding by as much as possible, and that the tube should be thoroughly flushed before administration.', '', '', 'DDInter', 0);
