INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (4891, 17912, 'Dronedarone', 'Grapefruit juice may increase the plasma concentrations of dronedarone.  The proposed mechanism is inhibition of CYP450 3A4-mediated first-pass metabolism in the gut wall by certain compounds present in grapefruits.  According to the product labeling, administration with grapefruit juice resulted in a 2.5-fold increase in dronedarone peak plasma concentration and a 3-fold increase in systemic exposure.  Because dronedarone is associated with concentration-dependent prolongation of the QT interval, increased levels may potentiate the risk of ventricular arrhythmias such as torsade de pointes and sudden death.', 'grapefruit juice', 'Major', 'Patients treated with dronedarone should avoid consumption of grapefruit, grapefruit juice, and any supplement containing grapefruit extract.  Dronedarone should be taken twice daily with the morning and evening meals.', '', '', 'DDInter', 0);
