INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (4932, 0, 'Eluxadoline', 'Consumption of more than 3 alcoholic beverages per day may increase the risk of acute pancreatitis during treatment with eluxadoline.', 'alcohol', 'Major', 'Chronic or acute excessive use of alcohol should be avoided during treatment with eluxadoline.  Alcoholism, alcohol abuse, alcohol addiction, and consumption of more than 3 alcoholic beverages per day are considered contraindications to the use of eluxadoline.  The product labeling recommends taking eluxadoline with food.  Patients should be advised to stop taking eluxadoline and seek medical attention if they experience potential symptoms of pancreatitis such as persistent nausea, vomiting, abdominal tenderness, and upper abdominal pain, especially that which is made worse after eating or radiates to the back or shoulders.', '', '', 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (4933, 0, 'Elvitegravir', 'Food enhances the oral bioavailabilities of both elvitegravir and tenofovir.  When a single dose of cobicistat/elvitegravir/emtricitabine/tenofovir (trade name Stribild) was given with a light meal (approximately 373 kcal; 20% fat), mean elvitegravir and tenofovir systemic exposures (AUCs) increased by 34% and 24%, respectively, relative to fasting conditions.  When administered with a high-fat meal (approximately 800 kcal; 50% fat), the mean AUC of elvitegravir and tenofovir increased by 87% and 23%, respectively, relative to fasting conditions.  The alterations in mean AUCs of cobicistat and emtricitabine were not clinically significant with either the light or high-fat meal.', 'high-fat foods', 'Moderate', 'Cobicistat/elvitegravir/emtricitabine/tenofovir as a fixed-dose preparation should be administered once daily with food.  Elvitegravir as a single-ingredient preparation should also be administered once daily with food.', '', '', 'DDInter', 0);
