INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (5000, 0, 'Encorafenib', 'Coadministration with potent or moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of encorafenib, which is primarily metabolized by the isoenzyme.', 'grapefruit juice', 'Major', 'Concomitant use of encorafenib with grapefruit or grapefruit juice should generally be avoided.  If coadministration is required, the manufacturer recommends reducing the encorafenib dose to one-third of the dose used prior to addition of a potent CYP450 3A4 inhibitor or one-half of the dose used prior to addition of a moderate CYP450 3A4 inhibitor.  After the inhibitor has been discontinued for 3 to 5 elimination half-lives, the encorafenib dose that was taken prior to initiating the inhibitor may be resumed.', '', '', 'DDInter', 0);