INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (5205, 15472, 'Esmolol', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation.  Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', 'alcohol', 'Moderate', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.  Some authorities recommend avoiding alcohol in patients receiving vasodilating antihypertensive drugs.  Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', '', '', 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (5206, 0, 'Esterified estrogens', 'Coadministration with grapefruit juice may increase the bioavailability of oral estrogens.  The proposed mechanism is inhibition of CYP450 3A4-mediated first-pass metabolism in the gut wall induced by certain compounds present in grapefruits.', 'food', 'Minor', 'It is not recommended to eat or drink foods containing grapefruit while taking any estrogen-based medications.', '', '', 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (5207, 4418, 'Estramustine', 'Food and milk may significantly decrease serum concentrations of estramustine.  The mechanism of the interaction is not known.', 'dairy products', 'Moderate', 'It is recommended that estramustine be taken at least 1 hour before or 2 hours after meals, and that it not be coadministered with milk, or calcium containing products.', '', '', 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (5208, 4438, 'Estradiol', 'Coadministration with grapefruit juice may increase the bioavailability of oral estrogens.  The proposed mechanism is inhibition of CYP450 3A4-mediated first-pass metabolism in the gut wall induced by certain compounds present in grapefruits.', 'grapefruit juice', 'Minor', 'Coadministration with grapefruit juice may increase the bioavailability of oral estrogens.  The proposed mechanism is inhibition of CYP450 3A4-mediated first-pass metabolism in the gut wall induced by certain compounds present in grapefruits.  In a small, randomized, crossover study, the administration of ethinyl estradiol with grapefruit juice (compared to herbal tea) increased peak plasma drug concentration (Cmax) by 37% and area under the concentration-time curve (AUC) by 28%.  Based on these findings, grapefruit juice is unlikely to affect the overall safety profile of ethinyl estradiol.  However, as with other drug interactions involving grapefruit juice, the pharmacokinetic alterations are subject to a high degree of interpatient variability.  Also, the effect on other estrogens has not been studied.', '', '', 'DDInter', 0);
