INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (5252, 0, 'Etomidate', 'Alcohol may potentiate some of the pharmacologic effects of central nervous system (CNS)-active agents.  Use in combination may result in additive CNS depression and/or impairment of judgment, thinking, and psychomotor skills.', 'alcohol', 'Moderate', 'Patients receiving CNS-active agents should be advised to avoid or limit consumption of alcohol.  Ambulatory patients should be counseled against driving, operating machinery, or engaging in potentially hazardous activities requiring mental alertness and motor coordination until they know how these agents affect them, and to notify their physician if they experience excessive or prolonged CNS effects that interfere with their normal activities.', '', '', 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (5253, 2361, 'Everolimus', 'Grapefruit juice may significantly increase the plasma concentrations of orally administered everolimus.  The mechanism is inhibition of CYP450 3A4 and P-glycoprotein activity in the gut wall by certain compounds present in grapefruit.', 'grapefruit juice', 'Moderate', 'Patients treated with everolimus should avoid consumption of grapefruit and grapefruit juice.', '', '', 'DDInter', 0);
