INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, reference_text, source, created_at, management_text_blob, mechanism_text_blob, interaction_blob) VALUES (2723, 6304, 'Selumetinib', 'Grapefruit juice may increase the plasma concentrations of selumetinib, which undergoes metabolism primarily by CYP450 3A4 and to a lesser extent by CYP450 2C19, 1A2, 2C9, 2E1 and 3A5, as well as glucuronidation by UGT1A1 and UGT1A3.  The proposed mechanism is inhibition of CYP450 3A4-mediated first-pass metabolism in the gut wall by certain compounds present in grapefruit.  Inhibition of hepatic CYP450 3A4 may also contribute.', 'grapefruit juice', 'Major', '', 'DDInter', 0, X'', NULL, X'');
