INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (5442, 0, 'Fomepizole', 'The manufacturer reports that oral doses of fomepizole (10 to 20 mg/kg) will significantly reduce the rate of elimination of moderate doses of ethanol by approximately 40% when given to healthy volunteers.  The mechanism is via alcohol dehydrogenase inhibition.  By a similar mechanism, ethanol will decrease the rate of elimination of fomepizole.', 'alcohol', 'Major', 'In general, ethanol and fomepizole should not be coadministered in the treatment of ethylene glycol poisoning.', '', '', 'DDInter', 0);
INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (5443, 8269, 'Fluphenazine', 'Concurrent use of ethanol and phenothiazines may result in additive CNS depression and psychomotor impairment.  Also, ethanol may precipitate dystonic reactions in patients who are taking phenothiazines.', 'alcohol', 'Moderate', 'Patients should be advised to avoid alcohol during phenothiazine therapy.', '', '', 'DDInter', 0);
