INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (5475, 0, 'Fosamprenavir', 'Food may reduce the systemic bioavailability of amprenavir from fosamprenavir oral suspension.  The mechanism of interaction has not been described.  According to the product labeling, administration of fosamprenavir oral suspension (1400 mg single dose) with a high-fat meal (967 kcal, 67 g fat, 33 g protein, 58 g carbohydrate) reduced amprenavir peak plasma concentration (Cmax) by 46% and systemic exposure (AUC) by 28% compared to administration in a fasted state.', 'high-fat foods', 'Moderate', 'Fosamprenavir suspension should be administered on an empty stomach in adults, but with food in pediatric patients to aid palatability and compliance.  If emesis occurs within 30 minutes after dosing the suspension, the dose should be repeated.  Fosamprenavir tablets may be taken with or without food.', '', '', 'DDInter', 0);
