INSERT OR REPLACE INTO food_interactions (id, med_id, trade_name, interaction, ingredient, severity, management_text, mechanism_text, reference_text, source, created_at) VALUES (5809, 306, 'Hydroxychloroquine', 'Theoretically, grapefruit and grapefruit juice may increase the plasma concentrations of hydroxychloroquine or chloroquine and the risk of toxicities such as QT interval prolongation and ventricular arrhythmias.  The proposed mechanism is inhibition of CYP450 3A4-mediated first-pass metabolism in the gut wall induced by certain compounds present in grapefruit.', 'grapefruit juice', 'Moderate', 'Although clinical data are lacking, it may be advisable to avoid the consumption of grapefruit, grapefruit juice, and any supplement containing grapefruit extract during hydroxychloroquine or chloroquine therapy.', '', '', 'DDInter', 0);
